    x = A[r][c]
    left, right = A[r][:c], A[r][c + 1:]
    if (x in left) or (x in right):
        return True


def in_col(A, r, c):
    x = A[r][c]
    for row in range(n):
        if row == r:
            continue
        if A[row][c] == x:
            return True

