    a, b = map(int,input().split())
    if a in front:
        front[a] += 1
    else:
        front[a] = 1

    if b != a:   
       if b in back:
          back[b] += 1
       else:
          back[b] = 1

ans = -1
m = dict()
for key, value in front.items():
    if value >= n/2:
        ans = 0
        break
    elif key in back and front[key] + back[key] >= n/2:
        m[key] = math.ceil(n/2) - front[key]

else:
    if len(m) > 0:
        ans = min(sorted(m.values()))
    else:
        for key, value in back.items():
            if value >= n/2:
                ans = math.ceil(n/2)
                break
        else:
            ans = -1
print(ans)

