n = int(input())

coodenadas = []
for i in range(n):
    x, y = [ int(i) for i in input().split() ]
    coodenadas.append({
        'x': { x },
        'y': { y }
    })

for i in range(n-1, -1, -1):
    j = i - 1
    while j >= 0 and i < len(coodenadas):
        if len(coodenadas[j]['x'].intersection(coodenadas[i]['x'])) or len(coodenadas[j]['y'].intersection(coodenadas[i]['y'])):
            coodenadas[i]['x'].update(coodenadas[j]['x'])
            coodenadas[i]['y'].update(coodenadas[j]['y'])
            coodenadas.pop(j)
        j -= 1

print(len(coodenadas) - 1)

