import sys


def digits(phone_number):
    if phone_number:
        phone_number = phone_number.rstrip('\r|\n')
        length = len(phone_number)

        # Not even.
        if length in [2, 3]:
            return phone_number
        elif length % 2:
            return phone_number[0:3] + "-" + digits(phone_number[3:])
        return phone_number[0:2] + "-" + digits(phone_number[2:])
    else:
        return phone_number

if __name__ == '__main__':
    data = []
    for line in sys.stdin:
        data.append(line)

    for l in data[1::2]:
        print(digits(l))

