from array import array

input = lambda: sys.stdin.readline().rstrip("\r\n")
inp = lambda dtype: [dtype(x) for x in input().split()]
inp_2d = lambda dtype, n: [dtype(input()) for _ in range(n)]
debug = lambda *x: print(*x, file=sys.stderr)
ceil1 = lambda a, b: (a + b - 1) // b
out, tests = [], 1


def solve(x, y) -> bool:
    passable = 0
    while x < n and y > -1:
        if passbe[x][y] and passen[x][y]:
            passable += 1

        x += 1
        y -= 1

    return passable == 1


for _ in range(tests):
    n, m = inp(int)
    a = [input() for _ in range(n)]
    passbe = [array('b', [0] * (m + 1)) for _ in range(n + 1)]
    passen = [array('b', [0] * (m + 1)) for _ in range(n + 1)]
    passbe[-1][0] = passen[-1][m - 1] = 1

    for i in range(n):
        for j in range(m):
            passbe[i][j] = (passbe[i - 1][j] or passbe[i][j - 1]) and (a[i][j] != '#')

    for i in range(n - 1, -1, -1):
        for j in range(m - 1, -1, -1):
            passen[i][j] = (passen[i + 1][j] or passen[i][j + 1]) and (a[i][j] != '#')

    if not passbe[n - 1][m - 1]:
        out.append(0)
        continue

    ans = 2
    for j in range(1, m):
        if solve(0, j):
            ans = 1
            break

    for j in range(1, n - 1):
        if solve(j, m - 1):
            ans = 1
            break

    out.append(ans)
print('\n'.join(map(str, out)))

