def f(n):
    ans = 0
    for i in range(20):
        ans += max(0, n - step[i] + 1)
    return ans
def g(n):
    ans = 0
    for i in range(20):
        q = max(0, n - step[i] + 1)
        ans += q * (q + 1) // 2 + q * max(0, n - (step[i] + q))
    return ans
