        current_station = 0
        stop_indices = []

        while current_station <= stops[-1] + 100:
            if current_fuel < 10:
                if current_station > stops[-1]:
                    stop_indices.append(current_station)
                    break
                else:
                    current_stop_index = -1
                    break
            else:
