                current_station += 1
                if current_station % 10 == 0:
                    if current_station in stops:
                        if current_station == stops[current_stop_index]:
                            current_fuel += alpha
                            current_stop_index += 1
                        else:
                            current_stop_index = -1
                            break
                    elif current_station > stops[-1]:
                        stop_indices.append(current_station)
                        break


    if len(stop_indices) == 0: continue
    if len(stop_indices) > 0: possible_next_stops.add(stop_indices[0])

