    elif n == stk[-1][0]:
        stk[-1][1] += 1
    else:
        while stk and stk[-1][0] < n:
            result += stk[-1][1] * (stk[-1][1] - 1) // 2 + stk[-1][1] * 2
            stk.pop()
        if n == stk[-1][0]:
            stk[-1][1] += 1
        else:
            stk.append([n, 1])

